@HiltViewModel
class DetailViewModel @Inject constructor() : BaseViewModel() {

    override var logTag: String = "DetailViewModel"

    private val _review = MutableLiveData<Review>()
    val review: LiveData<Review> = _review

    private val _reviewPoints = MutableLiveData<List<ReviewPoint>>()
    val reviewPoints: LiveData<List<ReviewPoint>> = _reviewPoints

    fun setReview(review: Review?) {
        val review2 = MockReview.mockReview()
        _review.value = review2
        review?.let {
            Log.d("review", it.toString())
            _review.value = it
        }
        _reviewPoints.value = listOf(
            ReviewPoint(ReviewType.HOST, PointType.from(3)),
            ReviewPoint(ReviewType.SAFETY, PointType.from(2)),
            ReviewPoint(ReviewType.DISTANCE, PointType.from(1)),
            ReviewPoint(ReviewType.CLEANNESS, PointType.from(1))
        )
    }
}

